parameters.query:
 SELECT pedido_id, user_id,   status, id_empresa
FROM pedidos
WHERE   pedido_id =   {{ $json.body.numero }} and id_empresa =  {{ $json.body.id_empresa}}

LIMIT 1;

update  
  pedidos
  set status = 'cancelado'
WHERE   pedido_id =  {{ $json.body.numero }}   and  id_empresa = {{ $json.body.id_empresa}} 
;

update  
  pedidos
  set status = '{{ $json.status }}'
WHERE   pedido_id = {{ $json.pedido_id }}   and  id_empresa = {{ $json.id_empresa }} 
;

